i(document).on("click", ".button-menu-mobile", function (t) {
    t.preventDefault();
    e.body.toggleClass("sidebar-enable");

    // Em telas >= 576px, alterna entre modo compacto e normal
    if (e.window.width() >= 576) {
        "condensed" === e.body.attr("data-leftbar-compact-mode")
            ? e.deactivateCondensedSidebar()
            : e.activateCondensedSidebar();
    }
});
